using ShineEngine;

/// <summary>
/// 移动装备消息(generated by shine)
/// </summary>
public class FuncSendMoveEquipResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncSendMoveEquip;
	
	public int fromSlot;
	
	public int toSlot;
	
	public FuncSendMoveEquipResponse()
	{
		_dataID=GameResponseType.FuncSendMoveEquip;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncSendMoveEquipResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.fromSlot=stream.readInt();
		
		this.toSlot=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.fromSlot=stream.readInt();
		
		this.toSlot=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("fromSlot");
		writer.sb.Append(':');
		writer.sb.Append(this.fromSlot);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("toSlot");
		writer.sb.Append(':');
		writer.sb.Append(this.toSlot);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getEquipContainerTool(funcID).onMoveEquipByServer(fromSlot,toSlot);
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.fromSlot=0;
		this.toSlot=0;
	}
	
}
