using ShineEngine;

/// <summary>
/// 推送移动物品(generated by shine)
/// </summary>
public class FuncSendMoveItemResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncSendMoveItem;
	
	/// <summary>
	/// 从某格子
	/// </summary>
	public int fromIndex;
	
	/// <summary>
	/// 到某格子
	/// </summary>
	public int toIndex;
	
	public FuncSendMoveItemResponse()
	{
		_dataID=GameResponseType.FuncSendMoveItem;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncSendMoveItemResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.fromIndex=stream.readInt();
		
		this.toIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.fromIndex=stream.readInt();
		
		this.toIndex=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("fromIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.fromIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("toIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.toIndex);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getItemContainerTool(funcID).onServerSwapItem(fromIndex,toIndex);
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.fromIndex=0;
		this.toIndex=0;
	}
	
}
