using ShineEngine;

/// <summary>
/// 使用物品结果消息(generated by shine)
/// </summary>
public class FuncUseItemResultResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncUseItemResult;
	
	/// <summary>
	/// 道具ID(使用的)
	/// </summary>
	public int itemID;
	
	/// <summary>
	/// 数目
	/// </summary>
	public int num;
	
	/// <summary>
	/// 结果
	/// </summary>
	public bool result;
	
	public FuncUseItemResultResponse()
	{
		_dataID=GameResponseType.FuncUseItemResult;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getBaseItemContainerTool(funcID).onUseItemResult(itemID,num,result);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncUseItemResultResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.itemID=stream.readInt();
		
		this.num=stream.readInt();
		
		this.result=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.itemID=stream.readInt();
		
		this.num=stream.readInt();
		
		this.result=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("itemID");
		writer.sb.Append(':');
		writer.sb.Append(this.itemID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("num");
		writer.sb.Append(':');
		writer.sb.Append(this.num);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("result");
		writer.sb.Append(':');
		writer.sb.Append(this.result);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.itemID=0;
		this.num=0;
		this.result=false;
	}
	
}
