using ShineEngine;

/// <summary>
/// 推送获取分段翻页显示内容(generated by shine)
/// </summary>
public class FuncRefreshSubsectionIndexResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncRefreshSubsectionIndex;
	
	/// <summary>
	/// 大组index
	/// </summary>
	public int subsectionIndex;
	
	/// <summary>
	/// 小组index
	/// </summary>
	public int subsectionSubIndex;
	
	public FuncRefreshSubsectionIndexResponse()
	{
		_dataID=GameResponseType.FuncRefreshSubsectionIndex;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncRefreshSubsectionIndexResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.subsectionIndex=stream.readInt();
		
		this.subsectionSubIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.subsectionIndex=stream.readInt();
		
		this.subsectionSubIndex=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("subsectionIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.subsectionIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("subsectionSubIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.subsectionSubIndex);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.subsectionIndex=0;
		this.subsectionSubIndex=0;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getSubsectionRankTool(funcID).onRefreshSubsectionIndex(subsectionIndex,subsectionSubIndex);
	}
	
}
