using ShineEngine;

/// <summary>
/// 更新成员职位(generated by shine)
/// </summary>
public class FuncRefeshTitleRoleGroupResponse:FuncPlayerRoleGroupSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncRefeshTitleRoleGroup;
	
	/// <summary>
	/// 成员id
	/// </summary>
	public long memberID;
	
	/// <summary>
	/// 职位
	/// </summary>
	public int title;
	
	public FuncRefeshTitleRoleGroupResponse()
	{
		_dataID=GameResponseType.FuncRefeshTitleRoleGroup;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncRefeshTitleRoleGroupResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.memberID=stream.readLong();
		
		this.title=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.memberID=stream.readLong();
		
		this.title=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("memberID");
		writer.sb.Append(':');
		writer.sb.Append(this.memberID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("title");
		writer.sb.Append(':');
		writer.sb.Append(this.title);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		roleGroup.onSetTitle(memberID,title);
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.memberID=0L;
		this.title=0;
	}
	
}
