using ShineEngine;

/// <summary>
/// 推送玩家离开玩家群消息(generated by shine)
/// </summary>
public class FuncSendPlayerLeaveRoleGroupResponse:FuncSResponse
{
	/// <summary>
	/// 方式
	/// </summary>
	public int type;
	
	public long groupID;
	
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncSendPlayerLeaveRoleGroup;
	
	public FuncSendPlayerLeaveRoleGroupResponse()
	{
		_dataID=GameResponseType.FuncSendPlayerLeaveRoleGroup;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncSendPlayerLeaveRoleGroupResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.groupID=stream.readLong();
		
		this.type=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getRoleGroupTool(funcID).onLeaveRoleGroup(groupID,type);
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.groupID=stream.readLong();
		
		this.type=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("groupID");
		writer.sb.Append(':');
		writer.sb.Append(this.groupID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("type");
		writer.sb.Append(':');
		writer.sb.Append(this.type);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.groupID=0L;
		this.type=0;
	}
	
}
