using ShineEngine;

/// <summary>
/// 推送绑定平台消息(generated by shine)
/// </summary>
public class SendBindPlatformResponse:GameResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.SendBindPlatform;
	
	/// <summary>
	/// uid
	/// </summary>
	public string uid;
	
	/// <summary>
	/// 平台
	/// </summary>
	public string platform;
	
	public SendBindPlatformResponse()
	{
		_dataID=GameResponseType.SendBindPlatform;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.role.onBindPlatform(uid,platform);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SendBindPlatformResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.uid=stream.readUTF();
		
		this.platform=stream.readUTF();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.uid=stream.readUTF();
		
		this.platform=stream.readUTF();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("uid");
		writer.sb.Append(':');
		writer.sb.Append(this.uid);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("platform");
		writer.sb.Append(':');
		writer.sb.Append(this.platform);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.uid="";
		this.platform="";
	}
	
}
