using ShineEngine;

/// <summary>
/// 推送清空全部任务(gm)(generated by shine)
/// </summary>
public class SendClearAllQuestByGMResponse:GameResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.SendClearAllQuestByGM;
	
	public SendClearAllQuestByGMResponse()
	{
		_dataID=GameResponseType.SendClearAllQuestByGM;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.quest.onClearAllQuest();
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SendClearAllQuestByGMResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
}
