using ShineEngine;

/// <summary>
/// 主角加技能消息(generated by shine)
/// </summary>
public class MUnitAddSkillResponse:MUnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.MUnitAddSkill;
	
	/// <summary>
	/// 技能数据
	/// </summary>
	public SkillData skill;
	
	public MUnitAddSkillResponse()
	{
		_dataID=GameResponseType.MUnitAddSkill;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "MUnitAddSkillResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData skillT=stream.readDataFullNotNull();
		if(skillT!=null)
		{
			if(skillT is SkillData)
			{
				this.skill=(SkillData)skillT;
			}
			else
			{
				this.skill=new SkillData();
				if(!(skillT.GetType().IsAssignableFrom(typeof(SkillData))))
				{
					stream.throwTypeReadError(typeof(SkillData),skillT.GetType());
				}
				this.skill.shadowCopy(skillT);
			}
		}
		else
		{
			this.skill=null;
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.skill=(SkillData)stream.readDataSimpleNotNull();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("skill");
		writer.sb.Append(':');
		if(this.skill!=null)
		{
			this.skill.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("SkillData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		mLogic.getFightLogic().addSkill(skill);
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.skill=null;
	}
	
}
