using ShineEngine;

/// <summary>
/// 主角移除buff(generated by shine)
/// </summary>
public class MUnitRemoveBuffResponse:MUnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.MUnitRemoveBuff;
	
	/// <summary>
	/// buff实例ID
	/// </summary>
	public int instanceID;
	
	public MUnitRemoveBuffResponse()
	{
		_dataID=GameResponseType.MUnitRemoveBuff;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		mLogic.getFightLogic().buff.removeBuffByInstanceID(instanceID);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "MUnitRemoveBuffResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.instanceID=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.instanceID=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("instanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.instanceID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.instanceID=0;
	}
	
}
