using ShineEngine;

/// <summary>
/// 控制单位移除组CD消息(generated by shine)
/// </summary>
public class MUnitRemoveGroupCDResponse:MUnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.MUnitRemoveGroupCD;
	
	/// <summary>
	/// 组ID
	/// </summary>
	public int groupID;
	
	public MUnitRemoveGroupCDResponse()
	{
		_dataID=GameResponseType.MUnitRemoveGroupCD;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		mLogic.getFightLogic().cd.removeGroupCD(groupID);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "MUnitRemoveGroupCDResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.groupID=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.groupID=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("groupID");
		writer.sb.Append(':');
		writer.sb.Append(this.groupID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.groupID=0;
	}
	
}
