using ShineEngine;

/// <summary>
/// 场景推送基类(generated by shine)
/// </summary>
public class SceneSResponse:GameResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.SceneS;
	
	public Scene scene;
	
	public SceneSResponse()
	{
		_dataID=GameResponseType.SceneS;
		setNeedRelease();
	}
	
	protected override void preExecute()
	{
		if((scene=GameC.scene.getScene())==null)
		{
			Ctrl.warnLog("收到场景类消息时,场景不存在");
			return;
		}

		execute();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SceneSResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
}
