using ShineEngine;

/// <summary>
/// 角色更新部分角色外显数据消息(generated by shine)
/// </summary>
public class CharacterRefreshPartRoleShowDataResponse:UnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.CharacterRefreshPartRoleShowData;
	
	/// <summary>
	/// 改变数据
	/// </summary>
	public RoleShowChangeData data;
	
	public CharacterRefreshPartRoleShowDataResponse()
	{
		_dataID=GameResponseType.CharacterRefreshPartRoleShowData;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		//更新数据
		unit.getUnitData().getCharacterIdentity().roleShowData.onChange(data);
		//更新显示
		unit.show.onRefreshRoleShowData(data);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CharacterRefreshPartRoleShowDataResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT is RoleShowChangeData)
			{
				this.data=(RoleShowChangeData)dataT;
			}
			else
			{
				this.data=new RoleShowChangeData();
				if(!(dataT.GetType().IsAssignableFrom(typeof(RoleShowChangeData))))
				{
					stream.throwTypeReadError(typeof(RoleShowChangeData),dataT.GetType());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.data=(RoleShowChangeData)stream.readDataSimpleNotNull();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("data");
		writer.sb.Append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("RoleShowChangeData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.data=null;
	}
	
}
