using ShineEngine;

/// <summary>
/// 回复技能失败消息(generated by shine)
/// </summary>
public class ReCUnitSkillFailedExResponse:ReCUnitSkillFailedResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.ReCUnitSkillFailedEx;
	
	/// <summary>
	/// 当前的种子序号
	/// </summary>
	public int seedIndex;
	
	public ReCUnitSkillFailedExResponse()
	{
		_dataID=GameResponseType.ReCUnitSkillFailedEx;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.system.setSeedIndex(seedIndex);

		base.execute();
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "ReCUnitSkillFailedExResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.seedIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.seedIndex=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("seedIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.seedIndex);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.seedIndex=0;
	}
	
}
