using ShineEngine;

/// <summary>
/// 刷新简版单位位置消息(generated by shine)
/// </summary>
public class RefreshSimpleUnitPosResponse:SceneSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.RefreshSimpleUnitPos;
	
	public int instanceID;
	
	public PosData pos;
	
	public DirData dir;
	
	public RefreshSimpleUnitPosResponse()
	{
		_dataID=GameResponseType.RefreshSimpleUnitPos;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "RefreshSimpleUnitPosResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.instanceID=stream.readInt();
		
		this.pos=(PosData)stream.createData(PosData.dataID);
		this.pos.readBytesFull(stream);
		
		this.dir=(DirData)stream.createData(DirData.dataID);
		this.dir.readBytesFull(stream);
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.instanceID=stream.readInt();
		
		this.pos=(PosData)stream.createData(PosData.dataID);
		this.pos.readBytesSimple(stream);
		
		this.dir=(DirData)stream.createData(DirData.dataID);
		this.dir.readBytesSimple(stream);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("instanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.instanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("pos");
		writer.sb.Append(':');
		if(this.pos!=null)
		{
			this.pos.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("PosData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("dir");
		writer.sb.Append(':');
		if(this.dir!=null)
		{
			this.dir.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("DirData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		UnitSimpleData data=scene.getBindVisionUnit(instanceID);

		if(data!=null)
		{
			data.pos.pos.copyPos(pos);
			data.pos.dir.copyDir(dir);

			scene.inout.simpleUnitPosChanged(data);
		}
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.instanceID=0;
		this.pos=null;
		this.dir=null;
	}
	
}
