using ShineEngine;

/// <summary>
/// 刷新单位造型消息(generated by shine)
/// </summary>
public class RefreshUnitAvatarResponse:UnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.RefreshUnitAvatar;
	
	/// <summary>
	/// 模型ID
	/// </summary>
	public int modelID;
	
	/// <summary>
	/// 改变组
	/// </summary>
	public IntIntMap parts;
	
	public RefreshUnitAvatarResponse()
	{
		_dataID=GameResponseType.RefreshUnitAvatar;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		unit.getUnitData().fightDataLogic.avatar.setAvatarByServer(modelID,parts);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "RefreshUnitAvatarResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.modelID=stream.readInt();
		
		int partsLen=stream.readLen();
		if(this.parts!=null)
		{
			this.parts.clear();
			this.parts.ensureCapacity(partsLen);
		}
		else
		{
			this.parts=new IntIntMap(partsLen);
		}
		
		IntIntMap partsT=this.parts;
		for(int partsI=partsLen-1;partsI>=0;--partsI)
		{
			int partsK;
			int partsV;
			partsK=stream.readInt();
			
			partsV=stream.readInt();
			
			partsT.put(partsK,partsV);
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.modelID=stream.readInt();
		
		int partsLen=stream.readLen();
		if(this.parts!=null)
		{
			this.parts.clear();
			this.parts.ensureCapacity(partsLen);
		}
		else
		{
			this.parts=new IntIntMap(partsLen);
		}
		
		IntIntMap partsT=this.parts;
		for(int partsI=partsLen-1;partsI>=0;--partsI)
		{
			int partsK;
			int partsV;
			partsK=stream.readInt();
			
			partsV=stream.readInt();
			
			partsT.put(partsK,partsV);
		}
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("modelID");
		writer.sb.Append(':');
		writer.sb.Append(this.modelID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("parts");
		writer.sb.Append(':');
		writer.sb.Append("Map<int,int>");
		if(this.parts!=null)
		{
			writer.sb.Append('(');
			writer.sb.Append(this.parts.size());
			writer.sb.Append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			if(!this.parts.isEmpty())
			{
				int partsKFreeValue=this.parts.getFreeValue();
				int[] partsKKeys=this.parts.getKeys();
				int[] partsVValues=this.parts.getValues();
				for(int partsKI=partsKKeys.Length-1;partsKI>=0;--partsKI)
				{
					int partsK=partsKKeys[partsKI];
					if(partsK!=partsKFreeValue)
					{
						int partsV=partsVValues[partsKI];
						writer.writeTabs();
						writer.sb.Append(partsK);
						
						writer.sb.Append(':');
						writer.sb.Append(partsV);
						
						writer.writeEnter();
					}
				}
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.Append("=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.modelID=0;
		this.parts=null;
	}
	
}
