using ShineEngine;

/// <summary>
/// 单位上载具消息(generated by shine)
/// </summary>
public class UnitGetOnVehicleResponse:UnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.UnitGetOnVehicle;
	
	public int vehicleInstanceID;
	
	public int index;
	
	public UnitGetOnVehicleResponse()
	{
		_dataID=GameResponseType.UnitGetOnVehicle;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "UnitGetOnVehicleResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.vehicleInstanceID=stream.readInt();
		
		this.index=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.vehicleInstanceID=stream.readInt();
		
		this.index=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("vehicleInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.vehicleInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("index");
		writer.sb.Append(':');
		writer.sb.Append(this.index);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		unit.move.onGetOnVehicle(vehicleInstanceID,index);
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.vehicleInstanceID=0;
		this.index=0;
	}
	
}
