using ShineEngine;

/// <summary>
/// 单位刷新buff剩余次数消息(generated by shine)
/// </summary>
public class UnitRefreshBuffLastNumResponse:UnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.UnitRefreshBuffLastNum;
	
	/// <summary>
	/// buff实例ID
	/// </summary>
	public int buffInstanceID;
	
	/// <summary>
	/// 剩余次数
	/// </summary>
	public int lastNum;
	
	public UnitRefreshBuffLastNumResponse()
	{
		_dataID=GameResponseType.UnitRefreshBuffLastNum;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		unit.fight.getBuffLogic().refreshBuffLastNumByServer(buffInstanceID,lastNum);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "UnitRefreshBuffLastNumResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.buffInstanceID=stream.readInt();
		
		this.lastNum=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.buffInstanceID=stream.readInt();
		
		this.lastNum=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("buffInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.buffInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("lastNum");
		writer.sb.Append(':');
		writer.sb.Append(this.lastNum);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.buffInstanceID=0;
		this.lastNum=0;
	}
	
}
