using ShineEngine;

/// <summary>
/// 单位移除buff(generated by shine)
/// </summary>
public class UnitRemoveBuffResponse:UnitSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.UnitRemoveBuff;
	
	/// <summary>
	/// buff实例ID
	/// </summary>
	public int buffInstanceID;
	
	public UnitRemoveBuffResponse()
	{
		_dataID=GameResponseType.UnitRemoveBuff;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		unit.fight.getBuffLogic().removeBuffByInstanceID(buffInstanceID);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "UnitRemoveBuffResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.buffInstanceID=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.buffInstanceID=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("buffInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.buffInstanceID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.buffInstanceID=0;
	}
	
}
