using ShineEngine;

/// <summary>
/// 返回搜索结果(generated by shine)
/// </summary>
public class ReSearchPlayerResponse:GameResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.ReSearchPlayer;
	
	/// <summary>
	/// 结果
	/// </summary>
	public ContactData contact;
	
	public ReSearchPlayerResponse()
	{
		_dataID=GameResponseType.ReSearchPlayer;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "ReSearchPlayerResponse";
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData contactT=stream.readDataFullNotNull();
		if(contactT!=null)
		{
			if(contactT is ContactData)
			{
				this.contact=(ContactData)contactT;
			}
			else
			{
				this.contact=new ContactData();
				if(!(contactT.GetType().IsAssignableFrom(typeof(ContactData))))
				{
					stream.throwTypeReadError(typeof(ContactData),contactT.GetType());
				}
				this.contact.shadowCopy(contactT);
			}
		}
		else
		{
			this.contact=null;
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.contact=(ContactData)stream.readDataSimpleNotNull();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("contact");
		writer.sb.Append(':');
		if(this.contact!=null)
		{
			this.contact.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("ContactData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.contact=null;
	}
	
}
