using ShineEngine;

/// <summary>
/// 推送申请添加好友消息(generated by shine)
/// </summary>
public class SendApplyAddFriendResponse:GameResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.SendApplyAddFriend;
	
	/// <summary>
	/// 申请数据
	/// </summary>
	public ApplyAddFriendData applyData;
	
	public SendApplyAddFriendResponse()
	{
		_dataID=GameResponseType.SendApplyAddFriend;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.friend.onApplyAddFriend(applyData);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SendApplyAddFriendResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData applyDataT=stream.readDataFullNotNull();
		if(applyDataT!=null)
		{
			if(applyDataT is ApplyAddFriendData)
			{
				this.applyData=(ApplyAddFriendData)applyDataT;
			}
			else
			{
				this.applyData=new ApplyAddFriendData();
				if(!(applyDataT.GetType().IsAssignableFrom(typeof(ApplyAddFriendData))))
				{
					stream.throwTypeReadError(typeof(ApplyAddFriendData),applyDataT.GetType());
				}
				this.applyData.shadowCopy(applyDataT);
			}
		}
		else
		{
			this.applyData=null;
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.applyData=(ApplyAddFriendData)stream.readDataSimpleNotNull();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("applyData");
		writer.sb.Append(':');
		if(this.applyData!=null)
		{
			this.applyData.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("ApplyAddFriendData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.applyData=null;
	}
	
}
