using ShineEngine;

/// <summary>
/// 每日消息(generated by shine)
/// </summary>
public class DailyResponse:GameResponse
{
	/// <summary>
	/// 下个每日时间
	/// </summary>
	public long nextDailyTime;
	
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.Daily;
	
	public DailyResponse()
	{
		_dataID=GameResponseType.Daily;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.system.getPartData().nextDailyTime=nextDailyTime;
		me.onDaily();
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "DailyResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.nextDailyTime=stream.readLong();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.nextDailyTime=stream.readLong();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("nextDailyTime");
		writer.sb.Append(':');
		writer.sb.Append(this.nextDailyTime);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.nextDailyTime=0L;
	}
	
}
