using ShineEngine;

/// <summary>
/// 拾取物品消息(generated by shine)
/// </summary>
public class CUnitPickUpItemRequest:CUnitRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=SceneBaseRequestType.CUnitPickUpItem;
	
	/// <summary>
	/// 目标物品实例ID
	/// </summary>
	public int targetInstanceID;
	
	public CUnitPickUpItemRequest()
	{
		_dataID=SceneBaseRequestType.CUnitPickUpItem;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CUnitPickUpItemRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.targetInstanceID);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.targetInstanceID);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("targetInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.targetInstanceID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.targetInstanceID=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static CUnitPickUpItemRequest create(int instanceID,int targetInstanceID)
	{
		CUnitPickUpItemRequest re=(CUnitPickUpItemRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.targetInstanceID=targetInstanceID;
		return re;
	}
	
}
