using ShineEngine;

/// <summary>
/// 客户端控制单位子弹结束(isClientDrivePosAndAttack下才有意义)(generated by shine)
/// </summary>
public class CUnitRemoveBulletRequest:CUnitRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=SceneBaseRequestType.CUnitRemoveBullet;
	
	/// <summary>
	/// 子弹实例ID
	/// </summary>
	public int bulletInstanceID;
	
	public CUnitRemoveBulletRequest()
	{
		_dataID=SceneBaseRequestType.CUnitRemoveBullet;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CUnitRemoveBulletRequest";
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("bulletInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.bulletInstanceID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.bulletInstanceID);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.bulletInstanceID);
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.bulletInstanceID=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static CUnitRemoveBulletRequest create(int instanceID,int bulletInstanceID)
	{
		CUnitRemoveBulletRequest re=(CUnitRemoveBulletRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.bulletInstanceID=bulletInstanceID;
		return re;
	}
	
}
