using ShineEngine;

/// <summary>
/// 推送副本类场景状态信息(generated by shine)
/// </summary>
public class SendBattleStateResponse:SceneSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=SceneBaseResponseType.SendBattleState;
	
	/// <summary>
	/// 副本当前状态
	/// </summary>
	public int state;
	
	/// <summary>
	/// 剩余tick时间(-1就是没在tick)
	/// </summary>
	public int timeTick;
	
	public SendBattleStateResponse()
	{
		_dataID=SceneBaseResponseType.SendBattleState;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		scene.battle.onRefreshBattleStateByServer(state,timeTick);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SendBattleStateResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.state=stream.readInt();
		
		this.timeTick=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.state=stream.readInt();
		
		this.timeTick=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("state");
		writer.sb.Append(':');
		writer.sb.Append(this.state);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("timeTick");
		writer.sb.Append(':');
		writer.sb.Append(this.timeTick);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.state=0;
		this.timeTick=0;
	}
	
}
