using ShineEngine;

/// <summary>
/// 离开场景(场景服)(generated by shine)
/// </summary>
public class LeaveSceneForSceneResponse:SceneResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=SceneResponseType.LeaveSceneForScene;
	
	public LeaveSceneForSceneResponse()
	{
		_dataID=SceneResponseType.LeaveSceneForScene;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "LeaveSceneForSceneResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		GameC.scene.onLeaveScene(false);
	}
	
}
