using ShineEngine;

/// <summary>
/// 预进入场景服务器就绪(场景服)(generated by shine)
/// </summary>
public class PreEnterSceneNextForSceneResponse:SceneResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=SceneResponseType.PreEnterSceneNextForScene;
	
	/// <summary>
	/// 预备信息
	/// </summary>
	public ScenePreInfoData preInfo;
	
	public PreEnterSceneNextForSceneResponse()
	{
		_dataID=SceneResponseType.PreEnterSceneNextForScene;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "PreEnterSceneNextForSceneResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData preInfoT=stream.readDataFullNotNull();
		if(preInfoT!=null)
		{
			if(preInfoT is ScenePreInfoData)
			{
				this.preInfo=(ScenePreInfoData)preInfoT;
			}
			else
			{
				this.preInfo=new ScenePreInfoData();
				if(!(preInfoT.GetType().IsAssignableFrom(typeof(ScenePreInfoData))))
				{
					stream.throwTypeReadError(typeof(ScenePreInfoData),preInfoT.GetType());
				}
				this.preInfo.shadowCopy(preInfoT);
			}
		}
		else
		{
			this.preInfo=null;
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.preInfo=(ScenePreInfoData)stream.readDataSimpleNotNull();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("preInfo");
		writer.sb.Append(':');
		if(this.preInfo!=null)
		{
			this.preInfo.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("ScenePreInfoData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.preInfo=null;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		GameC.scene.preEnterSceneNext(preInfo);
	}
	
}
