using ShineEngine;

/// <summary>
/// 组队(generated by shine)
/// </summary>
public class TeamPart:PlayerBasePart
{
	/** 数据 */
	private TeamPartData _d;
	
	private PlayerTeamTool _tool;
	
	public override void setData(BaseData data)
	{
		base.setData(data);
		
		_d=(TeamPartData)data;
	}
	
	/// <summary>
	/// 获取数据
	/// </summary>
	public TeamPartData getPartData()
	{
		return _d;
	}
	
	/// <summary>
	/// 构造函数(只在new后调用一次,再次从池中取出不会调用)
	/// </summary>
	public override void construct()
	{
		if(Global.teamRoleGroupID>0)
		{
			me.func.registFuncTool(_tool=GameC.factory.createPlayerTeamTool());
		}
	}
	
	/// <summary>
	/// 构造数据前
	/// </summary>
	protected override void beforeMakeData()
	{
		
	}
	
	/// <summary>
	/// 初始化(创建后刚调用,与dispose成对)
	/// </summary>
	public override void init()
	{
		
	}
	
	/// <summary>
	/// 析构(回池前调用,与init成对)
	/// </summary>
	public override void dispose()
	{
		
	}
	
	/// <summary>
	/// 从库中读完数据后(做数据的补充解析)(onNewCreate后也会调用一次)(主线程)
	/// </summary>
	public override void afterReadData()
	{
		
	}
	
	/// <summary>
	/// 功能开启(id:功能ID)
	/// </summary>
	public override void onFunctionOpen(int id)
	{
		
	}
	
	/// <summary>
	/// 功能关闭(id:功能ID)
	/// </summary>
	public override void onFunctionClose(int id)
	{
		
	}
	
	protected override BaseData createPartData()
	{
		return new TeamPartData();
	}
	
	/// <summary>
	/// 是否有队伍
	/// </summary>
	public bool hasTeam()
	{
		return _tool.getOnlyOne()!=null;
	}
	
}
