using ShineEngine;

namespace ShineEngine
{
	/// <summary>
	/// ping包第一步(generated by shine)
	/// </summary>
	public class PingRequest:ShineRequest
	{
		/// <summary>
		/// 数据类型ID
		/// </summary>
		public const int dataID=ShineRequestType.Ping;
		
		public int index;
		
		public PingRequest()
		{
			_dataID=ShineRequestType.Ping;
		}
		
		/// <summary>
		/// 获取数据类名
		/// </summary>
		public override string getDataClassName()
		{
			return "PingRequest";
		}
		
		/// <summary>
		/// 写入字节流(完整版)
		/// </summary>
		protected override void toWriteBytesFull(BytesWriteStream stream)
		{
			base.toWriteBytesFull(stream);
			
			stream.startWriteObj();
			
			stream.writeInt(this.index);
			
			stream.endWriteObj();
		}
		
		/// <summary>
		/// 写入字节流(简版)
		/// </summary>
		protected override void toWriteBytesSimple(BytesWriteStream stream)
		{
			base.toWriteBytesSimple(stream);
			
			stream.writeInt(this.index);
			
		}
		
		/// <summary>
		/// 转文本输出
		/// </summary>
		protected override void toWriteDataString(DataWriter writer)
		{
			base.toWriteDataString(writer);
			
			writer.writeTabs();
			writer.sb.Append("index");
			writer.sb.Append(':');
			writer.sb.Append(this.index);
			
			writer.writeEnter();
		}
		
		/// <summary>
		/// 回池
		/// </summary>
		protected override void toRelease(DataPool pool)
		{
			base.toRelease(pool);
			
			this.index=0;
		}
		
		protected override void copyData()
		{
			base.copyData();
		}
		
		/// <summary>
		/// 创建实例
		/// </summary>
		public static PingRequest create(int index)
		{
			PingRequest re=(PingRequest)BytesControl.createRequest(dataID);
			re.index=index;
			return re;
		}
		
	}
}
