using ShineEngine;

/// <summary>
/// 连接主动关闭(阻止重连)(generated by shine)
/// </summary>
public class SocketCloseRequest:ShineRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=ShineRequestType.SocketClose;
	
	public SocketCloseRequest()
	{
		_dataID=ShineRequestType.SocketClose;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SocketCloseRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static SocketCloseRequest create()
	{
		SocketCloseRequest re=(SocketCloseRequest)BytesControl.createRequest(dataID);
		return re;
	}
	
}
