using ShineEngine;

/// <summary>
/// 连接成功消息(generated by shine)
/// </summary>
public class SocketConnectSuccessRequest:ShineRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=ShineRequestType.SocketConnectSuccess;
	
	/// <summary>
	/// 连接id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 重连令牌
	/// </summary>
	public int token;
	
	public SocketConnectSuccessRequest()
	{
		_dataID=ShineRequestType.SocketConnectSuccess;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SocketConnectSuccessRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.token);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.token);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("token");
		writer.sb.Append(':');
		writer.sb.Append(this.token);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.id=0;
		this.token=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static SocketConnectSuccessRequest create(int id,int token)
	{
		SocketConnectSuccessRequest re=(SocketConnectSuccessRequest)BytesControl.createRequest(dataID);
		re.id=id;
		re.token=token;
		return re;
	}
	
}
