using ShineEngine;

/// <summary>
/// 连接主动关闭(阻止重连)(generated by shine)
/// </summary>
public class SocketCloseResponse:ShineResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=ShineResponseType.SocketClose;
	
	public SocketCloseResponse()
	{
		_dataID=ShineResponseType.SocketClose;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SocketCloseResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		//等待连接关闭
		socket.setOpenReconnect(false);
	}
	
}
