using ShineEngine;

/// <summary>
/// socket重连失败消息(generated by shine)
/// </summary>
public class SocketReconnectFailedResponse:ShineResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=ShineResponseType.SocketReconnectFailed;
	
	public SocketReconnectFailedResponse()
	{
		_dataID=ShineResponseType.SocketReconnectFailed;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SocketReconnectFailedResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		socket.onReconnectFailed();
	}
	
}
