namespace Shine
{
	/** 区服信息(generated by shine) */
	export class AreaInfoConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,AreaInfoConfig>;
		
		/** id */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 获取 */
		public static get(id:number):AreaInfoConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,AreaInfoConfig>):void
		{
			this._dic=dic;
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,AreaInfoConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
