namespace Shine
{
	/** 属性类型表(generated by shine) */
	export class AttributeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:AttributeConfig[];
		
		/** 属性id */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 当前属性的上限值ID */
		public currentMaxID:number;
		
		/** 自增属性对应ID */
		public increaseID:number;
		
		/** 是否为千分比属性 */
		public isPercent:boolean;
		
		/** 公式类型 */
		public formula:number[];
		
		/** 显示名 */
		public showName:string;
		
		/** 推送角色自己方式 */
		public sendSelfType:number;
		
		/** 简版单位数据是否需要 */
		public isSimpleUnitNeed:boolean;
		
		/** 当前属性默认空满 */
		public isCurrentDefaultFull:boolean;
		
		/** 显示名原值 */
		private _showName:string;
		
		/** 获取 */
		public static get(id:number):AttributeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:AttributeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:AttributeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():AttributeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.showName=stream.readUTF();
			
			var formulaLen:number=stream.readLen();
			this.formula=new Array<number>(formulaLen);
			var formulaT:number[]=this.formula;
			for(var formulaI:number=0;formulaI<formulaLen;++formulaI)
			{
				var formulaV:number;
				formulaV=stream.readInt();
				
				formulaT[formulaI]=formulaV;
			}
			
			this.isPercent=stream.readBoolean();
			
			this.increaseID=stream.readInt();
			
			this.currentMaxID=stream.readInt();
			
			this.isCurrentDefaultFull=stream.readBoolean();
			
			this.sendSelfType=stream.readInt();
			
			this.isSimpleUnitNeed=stream.readBoolean();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			if(this._showName==null)
				this._showName=this.showName;
			this.showName=LanguageConfig.getText(this._showName);
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
