namespace Shine
{
	/** 单位显示部件表(generated by shine) */
	export class AvatarPartConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,AvatarPartConfig>;
		
		/** 部件类型 */
		public type:number;
		
		/** 部件ID */
		public id:number;
		
		/** 资源 */
		public source:string;
		
		/** 资源(资源转) */
		public sourceT:number;
		
		/** 优先级 */
		public proirity:number;
		
		/** 获取 */
		public static get(type:number,id:number):AvatarPartConfig
		{
			return this._dic.get(type * 4294967296 + id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,AvatarPartConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,AvatarPartConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,AvatarPartConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.type=stream.readInt();
			
			this.id=stream.readInt();
			
			this.source=stream.readUTF();
			
			this.proirity=stream.readInt();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			this.sourceT=LoadControl.getResourceIDByName(this.source);
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
