namespace Shine
{
	/** 建筑表(generated by shine) */
	export class BuildingConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,BuildingConfig>;
		
		/** id */
		public id:number;
		
		/** 战斗单位ID */
		public fightUnitID:number;
		
		/** 名字 */
		public name:string;
		
		/** 组ID */
		public groups:number[];
		
		/** 是否自行建造 */
		public canAutoBuild:boolean;
		
		/** 名字原值 */
		private _name:string;
		
		/** 获取 */
		public static get(id:number):BuildingConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,BuildingConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,BuildingConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,BuildingConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.fightUnitID=stream.readInt();
			
			this.name=stream.readUTF();
			
			var groupsLen:number=stream.readLen();
			this.groups=new Array<number>(groupsLen);
			var groupsT:number[]=this.groups;
			for(var groupsI:number=0;groupsI<groupsLen;++groupsI)
			{
				var groupsV:number;
				groupsV=stream.readInt();
				
				groupsT[groupsI]=groupsV;
			}
			
			this.canAutoBuild=stream.readBoolean();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			if(this._name==null)
				this._name=this.name;
			this.name=LanguageConfig.getText(this._name);
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
