namespace Shine
{
	/** CD表(generated by shine) */
	export class CDConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,CDConfig>;
		
		/** id */
		public id:number;
		
		/** 所属组号 */
		public groups:number[];
		
		/** 冷却时间(ms) */
		public cd:number;
		
		/** 保留类型 */
		public keepType:number;
		
		/** 获取 */
		public static get(id:number):CDConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,CDConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,CDConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,CDConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			var groupsLen:number=stream.readLen();
			this.groups=new Array<number>(groupsLen);
			var groupsT:number[]=this.groups;
			for(var groupsI:number=0;groupsI<groupsLen;++groupsI)
			{
				var groupsV:number;
				groupsV=stream.readInt();
				
				groupsT[groupsI]=groupsV;
			}
			
			this.cd=stream.readInt();
			
			this.keepType=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
