namespace Shine
{
	/** 调用方式表(generated by shine) */
	export class CallWayConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,CallWayConfig>;
		
		/** 点 */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 描述 */
		public describe:string;
		
		/** 背包满时邮件ID */
		public mailID:number;
		
		/** 是否需要获得物品提示 */
		public needAddItemNotice:boolean;
		
		/** 获取 */
		public static get(id:number):CallWayConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,CallWayConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,CallWayConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,CallWayConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.describe=stream.readUTF();
			
			this.mailID=stream.readInt();
			
			this.needAddItemNotice=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
