namespace Shine
{
	/** 角色表(generated by shine) */
	export class CharacterConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,CharacterConfig>;
		
		/** id */
		public id:number;
		
		/** 头像 */
		public icon:string;
		
		/** 战斗单位ID */
		public fightUnitID:number;
		
		/** 职业 */
		public vocation:number;
		
		/** 性别 */
		public sex:number;
		
		/** 获取 */
		public static get(id:number):CharacterConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,CharacterConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,CharacterConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,CharacterConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.icon=stream.readUTF();
			
			this.fightUnitID=stream.readInt();
			
			this.vocation=stream.readInt();
			
			this.sex=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
