namespace Shine
{
	/** 国家地区表(登录用)(generated by shine) */
	export class CountryCodeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,CountryCodeConfig>;
		
		/** id */
		public id:number;
		
		/** 匹配的关键字组 */
		public marks:string[];
		
		/** 获取 */
		public static get(id:number):CountryCodeConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,CountryCodeConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,CountryCodeConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,CountryCodeConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			var marksLen:number=stream.readLen();
			this.marks=new Array<string>(marksLen);
			var marksT:string[]=this.marks;
			for(var marksI:number=0;marksI<marksLen;++marksI)
			{
				var marksV:string;
				marksV=stream.readUTF();
				
				marksT[marksI]=marksV;
			}
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
