namespace Shine
{
	/** 单位效果表(generated by shine) */
	export class EffectConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,EffectConfig>;
		
		/** effectID */
		public id:number;
		
		/** 是否一次性特效 */
		public isOnce:boolean;
		
		/** 是否三段特效 */
		public isSME:boolean;
		
		/** 是否朝向同步 */
		public isSyncDirection:boolean;
		
		/** 叠加上限 */
		public plusMax:number;
		
		/** 资源 */
		public source:string;
		
		/** 资源(资源转) */
		public sourceT:number;
		
		/** 声音 */
		public sound:string;
		
		/** 声音(资源转) */
		public soundT:number;
		
		/** 获取 */
		public static get(id:number):EffectConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,EffectConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,EffectConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,EffectConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.isOnce=stream.readBoolean();
			
			this.isSME=stream.readBoolean();
			
			this.isSyncDirection=stream.readBoolean();
			
			this.plusMax=stream.readInt();
			
			this.source=stream.readUTF();
			
			this.sound=stream.readUTF();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			this.sourceT=LoadControl.getResourceIDByName(this.source);
			this.soundT=LoadControl.getResourceIDByName(this.sound);
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
