namespace Shine
{
	/** 兑换表(generated by shine) */
	export class ExchangeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,ExchangeConfig>;
		
		/** 组id */
		public groupID:number;
		
		/** id */
		public id:number;
		
		/** 消耗id */
		public costID:number;
		
		/** 奖励id */
		public rewarID:number;
		
		/** 获取 */
		public static get(id:number):ExchangeConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,ExchangeConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,ExchangeConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,ExchangeConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.groupID=stream.readInt();
			
			this.costID=stream.readInt();
			
			this.rewarID=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
