namespace Shine
{
	/** 流程步骤类型(generated by shine) */
	export class FlowStepConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,FlowStepConfig>;
		
		/** id */
		public id:number;
		
		/** 描述 */
		public describe:string;
		
		/** 获取 */
		public static get(id:number):FlowStepConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,FlowStepConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,FlowStepConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,FlowStepConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.describe=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
