namespace Shine
{
	/** 字体表(程序用)(需国际化)(generated by shine) */
	export class FontConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:FontConfig[];
		
		/** id */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 简体中文 */
		public zh_CN:string;
		
		/** 美国英语 */
		public en_US:string;
		
		/** 获取 */
		public static get(id:number):FontConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:FontConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:FontConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():FontConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.zh_CN=stream.readUTF();
			
			this.en_US=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
