namespace Shine
{
	/** 功能表(generated by shine) */
	export class FunctionConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,FunctionConfig>;
		
		/** 功能id */
		public id:number;
		
		/** 显示名字 */
		public showName:string;
		
		/** 开关ID */
		public switchID:number;
		
		/** 获取 */
		public static get(id:number):FunctionConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,FunctionConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,FunctionConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,FunctionConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readShort();
			
			this.switchID=stream.readShort();
			
			this.showName=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
