namespace Shine
{
	/** 国际化表(generated by shine) */
	export class LanguageConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<string,LanguageConfig>;
		
		/** 键 */
		public key:string;
		
		/** 简体中文 */
		public zh_CN:string;
		
		/** 美国英语 */
		public en_US:string;
		
		/** 获取 */
		public static get(key:string):LanguageConfig
		{
			return this._dic.get(key);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<string,LanguageConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<string,LanguageConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<string,LanguageConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.key=stream.readUTF();
			
			this.zh_CN=stream.readUTF();
			
			this.en_US=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
		protected getStr(type:number):string
		{
			switch(type)
			{
				case LanguageType.Zh_CN:
					return this.zh_CN;
				case LanguageType.En_US:
					return this.en_US;
			}
	
			return null;
		}
		
		/** 获取文字 */
		public static getText(key:string):string
		{
			if(key.length==0)
				return key;
	
			var config:LanguageConfig;
			var re:string;
	
			if((config=this.get(key))!=null && (re=config.getStr(CommonSetting.languageType))!=null)
				return re;
	
			if(CommonSetting.useMultiLanguage)
			{
				Ctrl.throwError("找不到LanguageConfig",key);
			}
	
			return key;
		}
		
	}
}
