namespace Shine
{
	/** 加载资源表(generated by shine) */
	export class MarkResourceConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<string,MarkResourceConfig>;
		
		/** 资源路径 */
		public url:string;
		
		/** 类型 */
		public type:number;
		
		/** 获取 */
		public static get(url:string):MarkResourceConfig
		{
			return this._dic.get(url);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<string,MarkResourceConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<string,MarkResourceConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<string,MarkResourceConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.url=stream.readUTF();
			
			this.type=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
