namespace Shine
{
	/** 怪物表(generated by shine) */
	export class MonsterConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,MonsterConfig>;
		
		/** id */
		public id:number;
		
		/** 战斗单位ID */
		public fightUnitID:number;
		
		/** 掉落方式 */
		public dropType:number;
		
		/** 掉落范围方式 */
		public dropScopeType:number;
		
		/** 掉落判定方式 */
		public dropDecideType:number;
		
		/** 是否退回原点时回血 */
		public needRecoverAtBack:boolean;
		
		/** 名字 */
		public name:string;
		
		/** 名字原值 */
		private _name:string;
		
		/** 获取 */
		public static get(id:number):MonsterConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,MonsterConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,MonsterConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,MonsterConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.fightUnitID=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.dropType=stream.readInt();
			
			this.dropDecideType=stream.readInt();
			
			this.dropScopeType=stream.readInt();
			
			this.needRecoverAtBack=stream.readBoolean();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			if(this._name==null)
				this._name=this.name;
			this.name=LanguageConfig.getText(this._name);
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
