namespace Shine
{
	/** 怪物等级表(generated by shine) */
	export class MonsterLevelConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,MonsterLevelConfig>;
		
		/** id */
		public id:number;
		
		/** 等级 */
		public level:number;
		
		/** 击杀奖励id */
		public rewardID:number;
		
		/** 获取 */
		public static get(id:number,level:number):MonsterLevelConfig
		{
			return this._dic.get(id * 4294967296 + level);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,MonsterLevelConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,MonsterLevelConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,MonsterLevelConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.level=stream.readInt();
			
			this.rewardID=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
